import React, { Component } from 'react';
import Card from './common/card';
import ContentCard from './common/contentCard';

class Conteudo extends Component {
    state = {  
        counter: 0,
        resposta:''
    }

    handleSubmit = (e) => {
        e.preventDefault();

        console.log(e.nativeEvent.submitter);

        let counter = this.state.counter;
        counter++;
        this.setState({counter});
    }


    getContent = (catalogo) => {

        let conteudo = [];

        for(let i = 0; i<catalogo.length; i++){
            if(catalogo[i].topico ===  this.props.match.params.topico ){
                conteudo = catalogo[i].content;
            }
        }

        return conteudo;
    }

    render() { 

        const conteudo = this.getContent(this.props.catalogos);

        if(conteudo.length<=0){
            return(<Card mensagem = 'Em breve adicionaremos este conteÃºdo : )'/>);
        }

        return ( 

            <React.Fragment>
                <Card mensagem = {this.props.match.params.topico}/> 
                {conteudo.map((cont)=><ContentCard  onSubmit = {this.handleSubmit} 
                class = {this.state.counter>=conteudo.indexOf(cont)?"cardShow":"cardHide"}
                key = {conteudo.indexOf(cont)} 
                mensagem = {cont} 
                id = {conteudo.indexOf(cont)}/>
                )}
            </React.Fragment>
        )

    }
}

export default Conteudo;
