import React, { Component } from 'react';
import images from '../static/utils/images';
import '../static/css/login.css'
import axios from 'axios';
import Joi from 'joi';

class Login extends Component {

    state = { user:{
        userName:'',
        password:''
    }}

    validateUser(user){
        const schema = Joi.object({
            userName: Joi.string().max(70).min(3).required(),
            password: Joi.string().max(70).min(3).required(),

        });

        return schema.validate(user);
    }


    handleSubmit = async e => {

        const blankUser = {
            userName : '',
            password : ''
        };

        e.preventDefault();

        if(e.currentTarget.id === 'loginBtn'){

            const valid = this.validateUser(this.state.user);

            if(valid.error){
                alert(valid.error.message);
                this.setState({user:blankUser});
            }

            else{

                try{
                    const result  = await axios.post('http://localhost:9000/api/login', this.state.user);
                    console.log(result);
                    window.location.replace("http://localhost:3000");
                }
                catch(e){
                    alert('Usuário ou email já utilizado.');
                    console.log(e);
                }



            }
        }

        else{
            window.location.replace("http://localhost:3000/register");
        }



    }

    handleChange = e => {
        const newUser = {...this.state.user};

        newUser[e.currentTarget.name] = e.currentTarget.value;

        this.setState({user:newUser});
    }

    render() { 
        return (  


     <div className="containerLogin">
        <img id = "loginImage" src={images.login} alt="Logo"/>
        <div className="title">Oi Luigi!</div>
            <form className="form" onSubmit ={this.handleSubmit}>
                <label htmlFor="userName">Usuário</label>
                <input onChange = {this.handleChange} className = "loginInput" type="text" id="fname" name="userName"/>
                <label htmlFor="lname">Senha</label>
                <input onChange = {this.handleChange} className = "loginInput" type="text" id="lname" name="password"/>
            <div className="buttons">
                <input id = 'loginBtn' className = "submitBtn" type="submit" value="Entrar"/>
                <input id = 'registerBtn' className = "submitBtn" type="submit" value="Registrar"/>
            </div>
            </form>
      </div>

        );
    }
}

export default Login;
