import React, { Component } from 'react';
import '../../static/css/conteudo.css';

class ContentCard extends Component {
    state = { 
        concuido: false
     }
    render() { 

        if(this.state.concluido){
            return(

                <div id = {this.props.id} className = {this.props.class}>
                    <span>{this.props.mensagem.conteudo}</span>
                </div>

            )
        }

        return (
            <div id = {this.props.id} className = {this.props.class}>
                <span>{this.props.mensagem.conteudo}</span>
                <form className="form" onSubmit ={e => this.props.onSubmit({evento:e, resposta:this.props.mensagem.resposta})}>
                        <label htmlFor="senha">Coloque aqui sua resposta :)</label>
                        <input onChange = {(e) => this.props.onChange(e.target.value)} className = "answer" type="text" id="answer" name="answer"/>
                        <input id = 'loginBtn' className = "answerBtn" type="submit" name="Entrar" value="Enviar"/>
                </form>

            </div>
        )
    }
}

export default ContentCard;
