import React, { Component } from 'react';
import '../static/css/addMaterias.css';

class AddTopicos extends Component {

    state = {
        form : {
            materia:'',
            assunto:'',
            topico:'',
            preReqs:''
        }
    }

    handleSubmit = e => {
        e.preventDefault();
        console.log('Submited');
    }

    handleChange = e => {
        const newForm = {...this.state.form};
        newForm[e.currentTarget.name] = e.currentTarget.value;
        this.setState({form:newForm});
    }


    render() { 
        return ( 
            <div className = "addTopicos">
                <h2>Tela de adição de tópicos</h2>
                <form className = "addForm" onSubmit ={this.handleSubmit}>
                    <div className="addItem">
                        <label htmlFor="materia">Materia</label>
                        <input name = 'materia'  value = {this.state.form.materia} onChange = {this.handleChange} autoFocus className = "addInput" type="text" id="materia" name="materia"/>
                    </div>
                    <div className="addItem">
                        <label htmlFor="assunto">Assunto</label>
                        <input name = 'assunto' autoFocus className = "addInput"  type="text" id="assunto" name="assunto"/>
                    </div>
                    <div className="addItem">
                        <label htmlFor="topico">Tópico</label>
                        <input name = 'topico' className = "addInput"  type="text" id="topico" name="topico"/>
                    </div>
                    <div className="addItem">
                        <label htmlFor="preReq">Pré-Requisitos</label>
                        <input name = 'preReqs' autoFocus className = "addInput"  type="text" id="preReq" name="preReq"/>
                    </div>
                    <div className="addItem">
                        <input className = "submitBtn" type="submit" value="Salvar"/>
                    </div>
                </form>
            </div>
         );
    }
}

export default AddTopicos;
