import React from 'react';
import '../../static/css/conteudo.css';


const ContentCard = (props) => {

    return (
        <div id = {props.id} className = {props.class}>
            <span>{props.mensagem.conteudo}</span>
            <form className="form" onSubmit ={props.onSubmit}>
                    <label htmlFor="senha">Coloque aqui sua resposta :)</label>
                    <input onChange = {(e) => props.onChange({resposta: e.target.value, certaRes: props.mensagem.resposta})} className = "answer" type="text" id="answer" name="answer"/>
                    <input id = 'loginBtn' className = "answerBtn" type="submit" name="Entrar" value="Enviar"/>
            </form>
        </div>

    );
}

export default ContentCard;
