import React, { Component } from 'react';
import '../static/css/addMaterias.css';
import axios from 'axios';
import Joi from 'joi';

class Register extends Component {
    state = {  
        user : {
            userName: '',
            email: '',
            password:''
        }
    }

    validateEnter(topico){

        const schema = Joi.object({
            materia: Joi.string().max(70).min(3).required(),
            assunto: Joi.string().max(70).min(3).required(),
            topico:  Joi.string().max(70).min(3).required(),
            preReqs:Joi.array().required()
        })

        return schema.validate(topico);
    }

    handleSubmit = async e => {
        e.preventDefault();
        const valid = this.validateEnter(this.state.form);

        /*
        if(valid.error){
            alert(valid.error.message);
        }
        else{
        const result  = await axios.post('http://localhost:9000/api/topicos/add', this.state.form);
        alert("T칩pico adicionado com sucesso.");
        console.log(result);
        }
        */

    }

    handleChange = e => {
        const newUser = {...this.state.user};

        newUser[e.currentTarget.name] = e.currentTarget.value;

        this.setState({user:newUser});
    }

    render() { 
        return ( 

            <div className = "addTopicos">
                <h2>Tela de registro de usu치rio</h2>
                <form className = "addForm" onSubmit ={this.handleSubmit}>
                    <div className="addItem">
                        <label htmlFor="userName">Usu치rio</label>
                        <input name = 'userName'  value = {this.state.user.userName} onChange = {this.handleChange} autoFocus className = "addInput" type="text" id="userName" />
                    </div>
                    <div className="addItem">
                        <label htmlFor="password">Senha</label>
                        <input name = 'password' value = {this.state.user.password}  onChange = {this.handleChange} autoFocus className = "addInput"  type="text" id="password" />
                    </div>
                    <div className="addItem">
                        <label htmlFor="email">E-mail</label>
                        <input name = 'email' value = {this.state.user.email}  onChange = {this.handleChange} className = "addInput"  type="text" id="email" />
                    </div>
                    <div className="addItem">
                        <input className = "submitBtn" type="submit" value="Salvar"/>
                    </div>
                </form>
            </div>

         );
    }
}

export default Register;
